import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function ActivateUserPage() {
  let { userId, userToken } = useParams();

  useEffect(() => {
    // Замени URL на актуальный путь к твоему API
    const apiUrl = `http://127.0.0.1:8000/api/v1/confirm-email/${userId}/${userToken}`;

    axios.get(apiUrl)
      .then(response => {
        console.log('Ответ сервера:', response.data);
        // Здесь ты можешь обработать ответ сервера
      })
      .catch(error => {
        console.error('Ошибка при запросе:', error);
        // Здесь ты можешь обработать ошибку запроса
      });
  }, [userId, userToken]); // Зависимости useEffect

  return (
    <div>
      <h1>Активация учетной записи...</h1>
      {/* Здесь может быть дополнительный UI */}
    </div>
  );
}

export default ActivateUserPage;
