import React, { useState } from 'react';
import { EditorState } from 'draft-js';
import { useNavigate } from 'react-router-dom';
import EditorToolbar from '../service/editors-toolbar'
import API from '../service/axios'; 

const AddNote = () => {
  const [editorState, setEditorState] = useState(() => EditorState.createEmpty());
  const [title, setTitle] = useState('')
  const [isSuccess, setIsSuccess] = useState(false);
  const navigate = useNavigate();

  const handleSaveNote = async () => {
    // Here you would convert the editorState to HTML or text depending on your backend
    const noteContent = JSON.stringify(editorState); // Replace with actual conversion
    try {
      // You would adjust the API endpoint and data structure as per your backend requirements
      const response = await API.post('/notes',
       { content: noteContent 
    });
      if (response.status === 201) {
        setIsSuccess(true);
        setTimeout(() => {
          navigate('/notes'); // Redirect to the notes tab
        }, 2000); 
      }
    } catch (error) {
      console.error('Error saving note:', error);
    }
  };

  return (
    <div className="add-note-container">
    <h2>Add Note</h2>
    {isSuccess && <div className="success-message">Note created successfully!</div>}
    <input
      type="text"
      placeholder="Note Title"
      value={title}
      onChange={(e) => setTitle(e.target.value)}
      className="note-title-input"
    />
    <EditorToolbar
      editorState={editorState}
      setEditorState={setEditorState}
    />
    <button onClick={handleSaveNote} className="action-button">
      Save Note
    </button>
  </div>
  );
};

export default AddNote;
