import React, { useState } from 'react';
import axios from 'axios';

function LoginPage() {
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    // Отправьте данные на сервер для проверки логина и пароля.
    try {
      const response = await axios.post('http://your-backend-url/login', credentials);

      if (response.status === 200) {
        // Логин и пароль верны, сессия начинается.
        setError('');
        // Дополнительные действия после успешного входа.
      }
    } catch (error) {
      // Логин или пароль неверны, обработайте ошибку и покажите сообщение.
      if (error.response) {
        setError(error.response.data.message);
      } else {
        console.error('Login error:', error);
        setError('An error occurred while logging in.');
      }
    }
  };

  const handleForgotPassword = () => {
    // Реализуйте здесь логику для восстановления пароля, например, отправьте письмо на почту пользователя.
    // Пользователь должен ввести свой email для восстановления пароля.
  };

  return (
    <div className='login-page'>
      <form onSubmit={handleLogin}>
        <div className='form-group'>
          <label htmlFor='username'>Username</label>
          <input
            type='text'
            className='form-control'
            id='username'
            placeholder='Enter username'
            onChange={(e) => setCredentials({ ...credentials, username: e.target.value })}
          />
        </div>
        <div className='form-group'>
          <label htmlFor='password'>Password</label>
          <input
            type='password'
            className='form-control'
            id='password'
            placeholder='Enter password'
            onChange={(e) => setCredentials({ ...credentials, password: e.target.value })}
          />
        </div>
        <div>
        <button type='submit' className='btn btn-primary'>
          Login
        </button>
        <button className='btn btn-link' onClick={handleForgotPassword}>
            Forgot password
        </button>
        </div>
      </form>
      {error && <div className='alert alert-danger'>{error}</div>}
    </div>
  );
}

export default LoginPage;
