import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import API from '../axios';
import { useAuth } from '../authContext';
import "../../css/registration.css";

const ClientRegistrationPage = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [acceptTerms, setAcceptTerms] = useState(false);
  const [error, setError] = useState('');

  const location = useLocation();
  const navigate = useNavigate();
  const { accessToken } = location.state || {};

  useEffect(() => {
    if (!accessToken) {
        navigate('/login');
        return;
      }
    API.get(`/get-user`, { headers: { Authorization: `Bearer ${accessToken}` } })
      .then(response => {
        setFirstName(response.data[0].first_name);
        setLastName(response.data[0].last_name);
        setEmail(response.data[0].email);
      })
      .catch(error => {
        console.error('Error fetching user data', error);
      });
  }, [accessToken, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!acceptTerms) {
      setError('You must accept the terms and conditions.');
      return;
    }
    if(password.length < 8){
        setError("You must have at least 8 characters in your password.");
        return;
    }
    if (password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }

    try{
        const response = await API.put('update-client/', {
            first_name: firstName,
            last_name: lastName,
            email: email,
            password: password,
            confirm_password: confirmPassword,
            accept_policy: acceptTerms
        },{
            headers: {
                Authorization: `Bearer ${accessToken}`
            }
        })
        if(response.status === 200){
            setError("Updated Sueccessfully")
            setTimeout(() => {
                navigate('/')
            }, 1500)
        }
    }
    catch (error) {
        console.error('Error updating client:', error);
        setError(error.response?.data?.message || 'An error occurred during the client update.');
    }
  };

  return (
    <div className="registration-page">
      <form onSubmit={handleSubmit} className="registration-form">
        <h2>Welcome to InTouch!<br/> Set Your Password To Proceed</h2>
        <input
          type="text"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          placeholder="First Name"
          required
        />
        <input
          type="text"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          placeholder="Last Name"
          required
        />
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <input
          type="password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          placeholder="Confirm Password"
          required
        />
        <label>
          <input
            type="checkbox"
            checked={acceptTerms}
            onChange={(e) => setAcceptTerms(e.target.checked)}
          />
          Accept Terms and Conditions
        </label>
        {error && <div className="error-message">{error}</div>}
        <div className="form-buttons">
          <button type="submit">Set Password</button>
        </div>
      </form>
    </div>
  );
};

export default ClientRegistrationPage;
