import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../axios';
import { useAuth } from '../authContext';
import "../../css/app.css"

function ActivateUserPage() {
  let { userId, userToken } = useParams();
  const navigate = useNavigate();
  const { login } = useAuth();
  const [activationHandled, setActivationHandled] = useState(false);

  useEffect(() => {
    if (activationHandled) {
      return;
    }

    API.get(`confirm-email/${userId}/${userToken}`)
      .then(response => {
        const accessToken = response.data.access_token;
        const refreshToken = response.data.refresh_token;

        const currentUrl = window.location.href;
        console.log(currentUrl);
        if (currentUrl.includes('/activate-client')) {
          localStorage.setItem('accessToken', accessToken); 
          navigate(`/client-registration`);
        } else {
          // Иначе, выполняем вход пользователя
          login(accessToken, refreshToken);
          navigate('/');
        }
        setActivationHandled(true);
      })
      .catch(error => {
        console.error('Error activating your account', error);
      });
  }, [userId, userToken, navigate, activationHandled, login]);

  return (
    <div>
      <h1>Account Activation In Progress...</h1>
    </div>
  );
}

export default ActivateUserPage;
