import React, { useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../axios';
import "../../css/app.css"

function ActivateUserPage() {
  let { userId, userToken } = useParams();
  const navigate = useNavigate()

  useEffect(() => {
    API.get(`confirm-email/${userId}/${userToken}`)
      .then(response => {
        localStorage.setItem('accessToken', response.data.access_token);
        localStorage.setItem('refreshToken', response.data.refresh_token);
        
        // Проверяем, содержится ли 'activate-client' в текущем URL
        const currentUrl = window.location.href;
        if (currentUrl.includes('activate-client')) {
          navigate(`/client-registration`);
        } else {
          navigate('/'); 
        }
      })
      .catch(error => {
        console.error('Error activating your account', error);
      });
  // Добавляем window.location.href в массив зависимостей
  }, [userId, userToken, navigate, window.location.href]);

  return (
    <div>
      <h1>Account Activation In Progress...</h1>
    </div>
  );
}

export default ActivateUserPage;
