import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';

function ActivateUserPage() {
  let { userId, userToken } = useParams();

  useEffect(() => {
    // Здесь ты можешь выполнить запрос на активацию
    fetch(`api/v1/confirm-email/?userId=${userId}&userToken=${userToken}`)
      .then(response => response.json())
      .then(data => {
        // Обработка данных после активации
      })
      .catch(error => {
        console.error('Ошибка активации:', error);
      });
  }, [userId, userToken]);

  return (
    <div>
      <h1>Активация учетной записи...</h1>
      {/* Здесь может быть дополнительный UI */}
    </div>
  );
}

export default ActivateUserPage;
