import React, { useState } from "react";
import { BrowserRouter, Route, Outlet, Link } from "react-router-dom"; // Изменил импорты

import LoginPage from "./LoginPage";
import LeftPanel from "./LeftPanel";
import ClientPage from "./ClientPage";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <BrowserRouter>
      <nav>
        {isLoggedIn ? (
          <>
            <ul>
              <li>
                <Link to="/clients">Clients</Link>
              </li>
            </ul>
          </>
        ) : null}
      </nav>

      <Route path="/login">
        {isLoggedIn ? <Navigate to="/clients" /> : <LoginPage setIsLoggedIn={setIsLoggedIn} />}
      </Route>

      <Route path="/clients" element={<ClientPage />} />
    </BrowserRouter>
  );
}

export default App;
