import React, { useState } from 'react';
import EditorToolbar from '../service/editors-toolbar';
import styles from "../css/block.module.css";

const AssignmentBlock = ({ block, updateBlock, removeBlock }) => {
  const [title, setTitle] = useState(block.title);
  const [choices, setChoices] = useState(block.choices);

  if (block.type === 'text') {
    return (
      <div className="text-block">
        <button type="button" onClick={() => removeBlock(block.id)}>Remove Block</button>
        <EditorToolbar editorState={block.content} setEditorState={(newState) => updateBlock(block.id, newState, block.choices)} />
      </div>
    );
  }


  const handleTitleChange = (event) => {
    setTitle(event.target.value);
    updateBlock(block.id, block.content, choices, event.target.value);
  };

  const handleChoiceChange = (index, event) => {
    const newChoices = [...choices];
    newChoices[index] = event.target.value;
    setChoices(newChoices);
    updateBlock(block.id, block.content, newChoices, title);
  };

   const handleNewChoiceFocus = (event) => {
    if (event.target.value === '') {
      
      addChoice();
    }
  };

  const addChoice = (newChoice = '') => {
    const newChoices = [...choices, newChoice];
    setChoices(newChoices);
    updateBlock(block.id, block.content, newChoices, title);
  };

  const removeChoice = (index) => {
    const newChoices = choices.filter((_, i) => i !== index);
    setChoices(newChoices);
    updateBlock(block.id, block.content, newChoices, title);
  };

  return (
    <div className="choice block">
      <input
        type="text"
        value={title}
        onChange={handleTitleChange}
        placeholder="Write question here..."
        className={styles.titleInput}
      />
      {choices.map((choice, index) => (
        <div key={index} className={styles.choiceOption}>
          {block.type === 'multiple' ? (
            <input type="checkbox" name={`block-${block.id}-choice`} />
          ) : (
            <input type="radio" name={`block-${block.id}-choice`} />
          )}
          <input
            type="text"
            value={choice}
            onChange={(event) => handleChoiceChange(index, event)}
            placeholder={`Option ${index + 1}`}
            className={styles.choiceInput}
          />
          <button type="button" onClick={() => removeChoice(index)} className={styles.removeChoiceButton}><i className='fa fa-minus-circle'></i></button>
        </div>
      ))}
      <div className={styles.choiceOption}>
        <input type="radio" disabled style={{ opacity: 0.5 }} />
        <input
          type="text"
          value=''
          onFocus={handleNewChoiceFocus}
          placeholder="Add option..."
          className={styles.choiceInput}
          style={{ opacity: 0.5 }}
        />
      </div>
      <div className={styles.controlButtons}>
        <button type="button" onClick={() => removeBlock(block.id)} className={styles.removeBlockButton}><i className='fa fa-trash-o'></i></button>
      </div>
    </div>
  );
};

export default AssignmentBlock;
