import React, {useState} from 'react'
import "../css/assignments.css"
import { EditorState } from 'draft-js';
import Editor from 'draft-js-plugins-editor';
import createToolbarPlugin from 'draft-js-static-toolbar-plugin';
import 'draft-js-static-toolbar-plugin/lib/plugin.css';

const toolbarPlugin = createToolbarPlugin();
const { Toolbar } = toolbarPlugin;
const plugins = [toolbarPlugin];

const AddAssignment = () => {
  const [formName, setFormName] = useState('');
  const [description, setDescription] = useState('');
  const [type, setType] = useState('');
  const [language, setLanguage] = useState('');
  const [tags, setTags] = useState('');
  const [question, setQuestion] = useState('');
  

  const handleSubmit = (e) => {
    e.preventDefault();
    // Обработайте данные формы, например, отправьте на сервер или сохраните локально
  };

  return (
    <div className='assignments-page'>
        <header>
          <h1>Add Assignment</h1>
          <button className='add-assignment-button'>Save Assignment</button>
        </header>
        <div className='add-assignment-body'>
        <form onSubmit={handleSubmit} className="form-creator">
      <label>
        Name of form
        <input
          type="text"
          value={formName}
          onChange={(e) => setFormName(e.target.value)}
        />
      </label>
      <label>
        Description
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </label>
      <label>
        Type
        <select value={type} onChange={(e) => setType(e.target.value)}>
          {/* Добавьте сюда опции */}
        </select>
      </label>
      <label>
        Language
        <select value={language} onChange={(e) => setLanguage(e.target.value)}>
          {/* Добавьте сюда опции */}
        </select>
      </label>
      <label>
        Tags
        <input
          type="text"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
        />
      </label>
      <label>
        Question
        <textarea
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />
      </label>
      <button type="submit">Save assignment</button>
    </form>
        </div>
    </div>
  )
}

export default AddAssignment
