import React, {useState, useEffect} from 'react'
import { useNavigate } from 'react-router-dom'
import { useAuth } from '../service/authContext'
import API from '../service/axios'
import { AssignmentTile } from '../components/AssignmentTile'

const ClientsAssignments = () => {
    const {currentUser} = useAuth()
    const [currentTab, setCurrentTab] = useState("all")
    const [assignments, setAssignments] = useState([])
    const [filteredAssignments, setFilteredAssignments] = useState([])

    const navigate = useNavigate()

    useEffect(() => {
        const fetchAssignments = async () => {
            try{
                var response = await API.get('assignments-client/')
                console.log(response)
                response = response.data.filter(assignment => assignment.user === currentUser.id) 
                console.log(response)
            }
            catch(e){
                console.error(e.message)
            }
        }

        fetchAssignments()
    }, [navigate])

    useEffect(() => {
        let updatedAssignments = [...assignments];
    
        // Filter assignments based on status
        if (currentTab !== 'all') {
          updatedAssignments = updatedAssignments.filter(assignment => 
            assignment.status === assignmentStatus // Assuming 'status' field in assignment
          );
        }
    
        setFilteredAssignments(updatedAssignments);
      }, [currentTab, assignments]);

    return (
        <div className="assignments-page">
          <header>
            <h1>My Assignments</h1>
          </header>
          <div className="tabs">
            <button
              className={currentTab === 'all' ? 'active' : ''}
              onClick={() => setCurrentTab('all')}
            >
              All Assignments
            </button>
            <button
              className={currentTab === 'in_progress' ? 'active' : ''}
              onClick={() => setCurrentTab('in_progress')}
            >
              In Progress
            </button>
            <button
              className={currentTab === 'done' ? 'active' : ''}
              onClick={() => setCurrentTab('done')}
            >
              Done
            </button>
          </div>
          <div className='assignment-grid'>
            {filteredAssignments.length > 0 ? (
              filteredAssignments.map(assignment => (
                <AssignmentTile
                  key={assignment.id}
                  assignment={assignment}
                />
              ))
            ) : (
              <div className="nothing-to-show">You have no assignments. Contact your doctor.</div>
            )}
          </div>
        </div>
      );
}

export default ClientsAssignments
