import React, { useState } from 'react';
import { useAuth } from '../service/authContext';
import '../css/settings.css';

const SettingsPage = () => {
  const [activeTab, setActiveTab] = useState('profile');
  const {currentUser} = useAuth()

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className='settings-page'>
        <header>
            <h1>Settings</h1>
        </header>
        <div className="tabs">
            <button onClick={() => handleTabClick('profile')} className={activeTab === 'profile' ? 'active' : ''}>
                Profile
            </button>
            <button onClick={() => handleTabClick('security')} className={activeTab === 'security' ? 'active' : ''}>
                Security
            </button>
        </div>
        <div className="content">
            {activeTab === 'profile' && <ProfileTab />}
            {activeTab === 'security' && <SecurityTab />}
        </div>
    </div>
  )
}

const ProfileTab = () => {
  // Здесь будет логика для профиля пользователя
  return (
    <div className="profile-tab">
      {/* Содержимое вкладки профиля */}
      <h2>Full Name</h2>
      <input type="text" placeholder="Jane Claus" />
      {/* И так далее для каждого поля */}
    </div>
  )
}

const SecurityTab = () => {
  // Здесь будет логика для безопасности пользователя
  return (
    <div className="security-tab">
      {/* Содержимое вкладки безопасности */}
      <h2>Change Password</h2>
      <input type="password" placeholder="Current Password" />
      {/* И так далее для каждого поля */}
    </div>
  )
}

export default SettingsPage;
