import React, { useState } from 'react';
import { useAuth } from '../service/authContext';
import '../css/settings.css';

const SettingsPage = () => {
  const [activeTab, setActiveTab] = useState('profile');
  const {currentUser} = useAuth()

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className='settings-page'>
        <header>
            <img></img>
        </header>
        <div className="tabs">
            <button onClick={() => handleTabClick('profile')} className={activeTab === 'profile' ? 'active' : ''}>
                Profile
            </button>
            <button onClick={() => handleTabClick('security')} className={activeTab === 'security' ? 'active' : ''}>
                Security
            </button>
        </div>
        <div className="content">
            {activeTab === 'profile' && <ProfileTab />}
            {activeTab === 'security' && <SecurityTab />}
        </div>
    </div>
  )
}

const ProfileTab = () => {
  const {currentUser} = useAuth()
  const [userData, setUserData] = useState({
    firstName: currentUser.first_name || '',
    lastName: currentUser.last_name || '',
    email: currentUser.email || '',
    dateOfBirth: currentUser.date_of_birth || ''
  })
  const [selectedFile, setSelectedFile] = useState([])


  const handleFileSelect = (e) => {
    setSelectedFile(e.target.files[0])
  }


  const handleChange = (event) => {
    const {name, value} = event.target
    setUserData({
      ...userData, 
      [name]: value
    })
  }

  const handleSubmit = () => {
    //TODO: handle submit 
  }



  return (
    <div className="profile-tab">
      <div className="left-column">
        <img src={currentUser.photo || 'default-avatar.png'} alt="Profile" className="avatar" />
        <input type="file" onChange={handleFileSelect} />
      </div>
      <div className="right-column">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="firstName"
            value={userData.firstName}
            onChange={handleChange}
            placeholder="First Name"
          />
          <input
            type="text"
            name="lastName"
            value={userData.lastName}
            onChange={handleChange}
            placeholder="Last Name"
          />
          <input
            type="email"
            name="email"
            value={userData.email}
            onChange={handleChange}
            placeholder="Email"
          />
          <input
            type="date"
            name="dateOfBirth"
            value={userData.dateOfBirth}
            onChange={handleChange}
            placeholder="Date of Birth"
          />
          <button type="submit">Save Changes</button>
        </form>
      </div>
    </div>
  )
}

const SecurityTab = () => {
  // Здесь будет логика для безопасности пользователя
  return (
    <div className="security-tab">
      {/* Содержимое вкладки безопасности */}
      <h2>Change Password</h2>
      <input type="password" placeholder="Current Password" />
      {/* И так далее для каждого поля */}
    </div>
  )
}

export default SettingsPage;
