import React from 'react';
import { RichUtils } from 'draft-js';

function AlignLeftButton({ setEditorState, getEditorState }) {
  const onAlignClick = () => {
    setEditorState(RichUtils.toggleBlockType(
      getEditorState(),
      'align-left' 
    ));
  };

  return (
    <button onMouseDown={(e) => {
      e.preventDefault();
      onAlignClick();
    }}>
      Align Left
    </button>
  );
}

export default AlignLeftButton
