import React, { useState, useEffect } from "react";
import { Outlet, NavLink } from "react-router-dom"; // Заменили Link на NavLink
import "../css/app.css";
import { useUser } from "../service/UserContext";
import axios from "axios";

function App() {
  const { userData, setUserData } = useUser();
  const [userDetails, setUserDetails] = useState(null);

  useEffect(() => {
    if (userData.userId) {
      axios.get(`http://127.0.0.1:8000/api/v1/users/${userData.userId}`)
        .then(response => {
          const { first_name, last_name } = response.data;
          setUserDetails({ first_name, last_name });
        })
        .catch(error => {
          console.error('Error getting :', error);
          // Обработка ошибок, возможно здесь стоит редирект на страницу логина
        });
    }
  }, [userData.userId]);

  return (
    <div className="app-container">
      <div className="side-bar">
        <div className="user-profile">
          <img
            className="user-avatar"
            src="https://avatars.githubusercontent.com/aksmaxn4?s=120"
            alt="Something"
          />
          <h3>
            {userData.userId} <br /> {userData.userId}
          </h3>
        </div>
        <div className="menu upper">
          <nav>
            <ul>
              <li>
                <NavLink to={`/clients`} activeClassName="active">
                  Clients
                </NavLink>
              </li>
              <li>
                <NavLink to={`/assignments`} activeClassName="active">
                  Assignments
                </NavLink>
              </li>
              <li>
                <NavLink to={`/community`} activeClassName="active">
                  Community
                </NavLink>
              </li>
              <li>
                <NavLink to={`/storage`} activeClassName="active">
                  Storage
                </NavLink>
              </li>
            </ul>
          </nav>
        </div>
        <div className="menu lower">
          <nav>
            <ul>
              <li>
                <NavLink to={`/settings`} activeClassName="active">
                  Settings
                </NavLink>
              </li>
              <li>
                <NavLink to={`/support`} activeClassName="active">
                  Support
                </NavLink>
              </li>
              <li>
                <NavLink to={`/logout`} activeClassName="active">
                  Logout
                </NavLink>
              </li>
            </ul>
          </nav>
        </div>
      </div>
      <div className="content">
        <Outlet />
      </div>
    </div>
  );
}

export default App;
