import React, {useState} from 'react'
import { EditorState } from 'draft-js';
import EditorToolbar from '../service/editors-toolbar';
import "../css/assignments.css"

const AddAssignment = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [type, setType] = useState('');
  const [language, setLanguage] = useState('');
  const [tags, setTags] = useState('');
  const [editorState, setEditorState] = useState(EditorState.createEmpty());

  const handleSubmit = (e) => {
    e.preventDefault();
    // Обработайте данные формы, например, отправьте на сервер или сохраните локально
  };

  const addBlock = (type) => {
    const newBlock = {
      id: blocks.length + 1, // Уникальный ID блока
      type: type,
      title: type === 'text' ? "Question" : '',
      text: '', 
      choices: [''], 
    };
    setBlocks([...blocks, newBlock]);
  };

  const updateBlock = (blockId, newTitle, newChoices) => {
    const updatedBlocks = blocks.map((block) => {
      if (block.id === blockId) {
        return { ...block, title: newTitle, choices: newChoices };
      }
      return block;
    });
    setBlocks(updatedBlocks);
  };
  
  

  return (
    <div className='assignments-page'>
        <header>
          <h1>Add Assignment</h1>
          <button className='add-assignment-button'>Save Assignment</button>
        </header>
        <div className='add-assignment-body'>
      <form onSubmit={handleSubmit} className="form-creator">
        <div className='form-title'>
          <input
          type='text'
          class="title-input"
          placeholder='Name of form'
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          >
          </input>
        <input
          class="title-input"
          placeholder="Description..."
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        ></input>
        </div>
      <label>
        Type
        <select value={type} onChange={(e) => setType(e.target.value)}>
          {/* Добавьте сюда опции */}
        </select>
      </label>
      <label>
        Language
        <select value={language} onChange={(e) => setLanguage(e.target.value)}>
          {/* Добавьте сюда опции */}
        </select>
      </label>
      <label>
        Tags
        <input
          type="text"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
        />
      </label>
      <div className='question-block'>
        <label>
          Question
          <EditorToolbar editorState={editorState} setEditorState={setEditorState} />
          <button type="submit">Save assignment</button>
        </label>
      </div>
    </form>
      <div className='block-buttons'>

      </div>
        </div>
    </div>
  )
}

export default AddAssignment
