import React, {useState} from 'react'
import { EditorState } from 'draft-js';
import EditorToolbar from '../service/editors-toolbar';
import "../css/assignments.css"

const AddAssignment = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [type, setType] = useState('');
  const [language, setLanguage] = useState('');
  const [tags, setTags] = useState('');
  const [editorState, setEditorState] = useState(EditorState.createEmpty());


  const [blocks, setBlocks] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Обработайте данные формы, например, отправьте на сервер или сохраните локально
  };

  const addBlock = (type) => {
    const newBlock = {
      id: blocks.length + 1,
      type: type,
      title: '', // Заголовок вопроса
      content: type === 'text' ? EditorState.createEmpty() : '',
      choices: type === 'text' ? [] : [''] // Пустой массив для вариантов ответа
    };
    setBlocks([...blocks, newBlock]);
  };

  const removeBlock = (blockId) => {
    const updatedBlocks = blocks.filter(block => block.id !== blockId);
    setBlocks(updatedBlocks);
  };

  const updateBlock = (blockId, newContent, newChoices, newTitle) => {
    const updatedBlocks = blocks.map((block) => {
      if (block.id === blockId) {
        return {
          ...block,
          content: newContent || block.content,
          choices: newChoices || block.choices,
          title: newTitle || block.title
        };
      }
      return block;
    });
    setBlocks(updatedBlocks);
  };
  
  

  return (
    <div className='assignments-page'>
      <header>
        <h1>Add Assignment</h1>
        <button className='add-assignment-button'>Save Assignment</button>
      </header>
      <div className='add-assignment-body'>
        <form onSubmit={handleSubmit} className="form-creator">
          <div className='form-title'>
            <input
              type='text'
              className="title-input" // Исправьте class на className
              placeholder='Name of form'
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <input
              type='text'
              className="title-input" // Исправьте class на className
              placeholder="Description..."
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </div>
          <label>
            Type
            <select value={type} onChange={(e) => setType(e.target.value)}>
              {/* Добавьте сюда опции */}
            </select>
          </label>
          <label>
            Language
            <select value={language} onChange={(e) => setLanguage(e.target.value)}>
              {/* Добавьте сюда опции */}
            </select>
          </label>
          <label>
            Tags
            <input
              type="text"
              value={tags}
              onChange={(e) => setTags(e.target.value)}
            />
          </label>
          {blocks.map(block => (
            <AssignmentBlock
              key={block.id}
              block={block}
              updateBlock={updateBlock}
              removeBlock={removeBlock}
            />
          ))}
          <button type="submit">Save assignment</button>
        </form>
        <div className='block-buttons'>
          <button type="button" onClick={() => addBlock('text')}>Add Text Block</button>
          <button type="button" onClick={() => addBlock('multiple')}>Add Multiple Choice Block</button>
          <button type="button" onClick={() => addBlock('single')}>Add Single Choice Block</button>
        </div>
      </div>
    </div>
  );
  
}

export default AddAssignment
