import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import API from '../axios';
import "../../css/registration.css";

const ClientRegistrationPage = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [acceptTerms, setAcceptTerms] = useState(false);
  const [error, setError] = useState('');

  const location = useLocation();
  const navigate = useNavigate();
  const { accessToken, refreshToken } = location.state || {};

  useEffect(() => {
    if (!accessToken) {
        // Если нет токена доступа, переадресация на страницу логина
        navigate('/login');
        return;
      }
    API.get(`/get-user`, { headers: { Authorization: `Bearer ${accessToken}` } })
      .then(response => {
        console.log(response)
        setFirstName(response.data[0].first_name);
        setLastName(response.data[0].last_name);
      })
      .catch(error => {
        console.error('Error fetching user data', error);
      });
  }, [accessToken, navigate]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!acceptTerms) {
      setError('You must accept the terms and conditions.');
      return;
    }
    if (password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }
    console.log('Form submitted');
  };

  return (
    <div className="registration-page">
      <form onSubmit={handleSubmit} className="registration-form">
        <h2>Set Your Password</h2>
        <input
          type="text"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          placeholder="First Name"
          required
          readOnly // Имя только для чтения, так как оно предзаполнено
        />
        <input
          type="text"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          placeholder="Last Name"
          required
          readOnly // Фамилия только для чтения, так как она предзаполнена
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <input
          type="password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          placeholder="Confirm Password"
          required
        />
        <label>
          <input
            type="checkbox"
            checked={acceptTerms}
            onChange={(e) => setAcceptTerms(e.target.checked)}
          />
          Accept Terms and Conditions
        </label>
        {error && <div className="error-message">{error}</div>}
        <div className="form-buttons">
          <button type="submit">Set Password</button>
        </div>
      </form>
    </div>
  );
};

export default ClientRegistrationPage;
